mapper.select:
select count(*)
from member
where userid = #{checkid}

mapper.update:
		update member 
		set username = #{mname},
    		userpass = #{mpass},
    		useremail = #{memail}
		where userid = #{mid}

mapper.select:

select b.* 
from(
    select rownum as rn, a.* 
    from(
        select bd.*
        from board bd,wish ws,board_list bl
        where bd.seq=ws.seq and bd.bcode = bl.bcode
         and ws.userid = #{userid}
         and bl.ccode = 2
        order by ws.wseq desc) a
    where rownum <= #{endNum}) b
where b.rn >= #{startNum}


select count(*) as scount from schedule a, board b
where a.seq = b.seq
and b.userid = #{userid}
